mapper.select:
select g.SC_SerierNO,g.SC_DoorNO,g.SC_CardGuidNO,g.SC_EventTypeID ,g.SC_InOutStatus,g.SC_AddTime ,b.SC_WorkerNO,b.SC_Name,b.SC_MobileNO,b.SC_IDTypeID,b.SC_DepartmentID ,c.SC_DepartmentName ,d.SC_CheckResultName ,e.SC_DeviceAreaID ,f.SC_DeviceAreaName from (
select a.SC_SerierNO,a.SC_DoorNO,a.SC_CardGuidNO,a.SC_EventTypeID ,a.SC_InOutStatus,a.SC_AddTime from SC_AccessRecord a where a.SC_SerierNO>#{scSerierno,jdbcType=BIGINT}
) g,SC_Employee b ,SC_Department c ,SC_ESDCheckResult d ,SC_Device e ,SC_DeviceArea f where 1=1 and RIGHT('000'+convert(varchar(20),g.SC_CardGuidNO),10) = b.SC_MobileNO and b.SC_DepartmentID=c.SC_DepartmentID and g.SC_EventTypeID = d.SC_CheckResultID and g.SC_DoorNO=e.SC_SerierNO and e.SC_DeviceAreaID=f.SC_DeviceAreaID order by g.SC_SerierNO desc

select g.SC_SerierNO,g.SC_DoorNO,g.SC_CardGuidNO,g.SC_EventTypeID ,g.SC_InOutStatus,g.SC_AddTime ,b.SC_WorkerNO,b.SC_Name,b.SC_MobileNO,b.SC_IDTypeID,b.SC_DepartmentID ,c.SC_DepartmentName ,d.SC_CheckResultName ,e.SC_DeviceAreaID ,f.SC_DeviceAreaName from (
select top 10 a.SC_SerierNO,a.SC_DoorNO,a.SC_CardGuidNO,a.SC_EventTypeID ,a.SC_InOutStatus,a.SC_AddTime from SC_AccessRecord a order by a.SC_SerierNO desc
) g,SC_Employee b ,SC_Department c ,SC_ESDCheckResult d ,SC_Device e ,SC_DeviceArea f where 1=1 and RIGHT('000'+convert(varchar(20),g.SC_CardGuidNO),10) = b.SC_MobileNO and b.SC_DepartmentID=c.SC_DepartmentID and g.SC_EventTypeID = d.SC_CheckResultID and g.SC_DoorNO=e.SC_SerierNO and e.SC_DeviceAreaID=f.SC_DeviceAreaID order by g.SC_SerierNO desc

select a.SC_SerierNO,a.SC_DoorNO,a.SC_CardGuidNO,a.SC_EventTypeID ,a.SC_InOutStatus,a.SC_AddTime ,b.SC_WorkerNO,b.SC_Name,b.SC_MobileNO,b.SC_IDTypeID,b.SC_DepartmentID ,c.SC_DepartmentName ,d.SC_CheckResultName ,e.SC_DeviceAreaID ,f.SC_DeviceAreaName from (
SELECT t.SC_SerierNO,t.SC_DoorNO,t.SC_CardGuidNO,t.SC_EventTypeID ,t.SC_InOutStatus,t.SC_AddTime FROM ( SELECT SC_CardGuidNO, MAX(SC_AddTime) as SC_AddTime FROM SC_AccessRecord where SC_AddTime>dateadd(hour,-8,GETDATE()) GROUP BY SC_CardGuidNO ) r INNER JOIN SC_AccessRecord t ON t.SC_CardGuidNO = r.SC_CardGuidNO AND t.SC_AddTime = r.SC_AddTime
) a,SC_Employee b ,SC_Department c ,SC_ESDCheckResult d,SC_Device e,SC_DeviceArea f where RIGHT('000'+convert(varchar(20),a.SC_CardGuidNO),10) = b.SC_MobileNO and b.SC_DepartmentID=c.SC_DepartmentID and a.SC_DoorNO=e.SC_SerierNO and e.SC_DeviceAreaID=f.SC_DeviceAreaID and a.SC_EventTypeID = d.SC_CheckResultID

select g.SC_SerierNO,g.SC_DoorNO,g.SC_CardGuidNO,g.SC_EventTypeID ,g.SC_InOutStatus,g.SC_AddTime ,b.SC_WorkerNO,b.SC_Name,b.SC_MobileNO,b.SC_IDTypeID,b.SC_DepartmentID ,c.SC_DepartmentName ,d.SC_CheckResultName ,e.SC_DeviceAreaID ,f.SC_DeviceAreaName ,ROW_NUMBER() over(order by g.SC_SerierNO desc) row from (
select a.SC_SerierNO,a.SC_DoorNO,a.SC_CardGuidNO,a.SC_EventTypeID ,a.SC_InOutStatus,a.SC_AddTime from SC_AccessRecord a where 1=1
and a.SC_AddTime between #{time1,jdbcType=TIMESTAMP} and #{time2,jdbcType=TIMESTAMP}
and a.SC_DoorNO in (1,5,6,13,15,7,16)
and a.SC_DoorNO in (2,3,8,12,14,9)
and a.SC_DoorNO in (4,10,11)
) g,SC_Employee b ,SC_Department c ,SC_ESDCheckResult d ,SC_Device e ,SC_DeviceArea f where 1=1 and RIGHT('000'+convert(varchar(20),g.SC_CardGuidNO),10) = b.SC_MobileNO
and b.SC_WorkerNO = #{jobX,jdbcType=NVARCHAR}
and b.SC_Name = #{nameX,jdbcType=NVARCHAR}
and b.SC_DepartmentID = #{departmentx,jdbcType=INTEGER}
and b.SC_DepartmentID=c.SC_DepartmentID and g.SC_EventTypeID = d.SC_CheckResultID and g.SC_DoorNO=e.SC_SerierNO and e.SC_DeviceAreaID=f.SC_DeviceAreaID

select * from(
select g.SC_SerierNO,g.SC_DoorNO,g.SC_CardGuidNO,g.SC_EventTypeID ,g.SC_InOutStatus,g.SC_AddTime ,b.SC_WorkerNO,b.SC_Name,b.SC_MobileNO,b.SC_IDTypeID,b.SC_DepartmentID ,c.SC_DepartmentName ,d.SC_CheckResultName ,e.SC_DeviceAreaID ,f.SC_DeviceAreaName ,ROW_NUMBER() over(order by g.SC_SerierNO desc) row from (
select a.SC_SerierNO,a.SC_DoorNO,a.SC_CardGuidNO,a.SC_EventTypeID ,a.SC_InOutStatus,a.SC_AddTime from SC_AccessRecord a where 1=1
and a.SC_AddTime between #{time1,jdbcType=TIMESTAMP} and #{time2,jdbcType=TIMESTAMP}
and a.SC_DoorNO in (1,5,6,13,15,7,16)
and a.SC_DoorNO in (2,3,8,12,14,9)
and a.SC_DoorNO in (4,10,11)
) g,SC_Employee b ,SC_Department c ,SC_ESDCheckResult d ,SC_Device e ,SC_DeviceArea f where 1=1 and RIGHT('000'+convert(varchar(20),g.SC_CardGuidNO),10) = b.SC_MobileNO
and b.SC_WorkerNO = #{jobX,jdbcType=NVARCHAR}
and b.SC_Name = #{nameX,jdbcType=NVARCHAR}
and b.SC_DepartmentID = #{departmentx,jdbcType=INTEGER}
and b.SC_DepartmentID=c.SC_DepartmentID and g.SC_EventTypeID = d.SC_CheckResultID and g.SC_DoorNO=e.SC_SerierNO and e.SC_DeviceAreaID=f.SC_DeviceAreaID
) t where t.row>=#{minRow,jdbcType=INTEGER} and #{maxRow,jdbcType=INTEGER}>=t.row

select count(*) total from (
select a.SC_SerierNO,a.SC_DoorNO,a.SC_CardGuidNO,a.SC_EventTypeID ,a.SC_InOutStatus,a.SC_AddTime from SC_AccessRecord a where 1=1
and a.SC_AddTime between #{time1,jdbcType=TIMESTAMP} and #{time2,jdbcType=TIMESTAMP}
and a.SC_DoorNO in (1,5,6,13,15,7,16)
and a.SC_DoorNO in (2,3,8,12,14,9)
and a.SC_DoorNO in (4,10,11)
) g,SC_Employee b ,SC_Department c ,SC_ESDCheckResult d ,SC_Device e ,SC_DeviceArea f where 1=1 and RIGHT('000'+convert(varchar(20),g.SC_CardGuidNO),10) = b.SC_MobileNO
and b.SC_WorkerNO = #{jobX,jdbcType=NVARCHAR}
and b.SC_Name = #{nameX,jdbcType=NVARCHAR}
and b.SC_DepartmentID = #{departmentx,jdbcType=INTEGER}
and b.SC_DepartmentID=c.SC_DepartmentID and g.SC_EventTypeID = d.SC_CheckResultID and g.SC_DoorNO=e.SC_SerierNO and e.SC_DeviceAreaID=f.SC_DeviceAreaID

select a.SC_SerierNO,a.SC_DoorNO,a.SC_CardGuidNO,a.SC_EventTypeID ,a.SC_InOutStatus,a.SC_AddTime ,b.SC_WorkerNO,b.SC_Name,b.SC_MobileNO,b.SC_IDTypeID,b.SC_DepartmentID ,c.SC_DepartmentName from SC_AccessRecord a,SC_Employee b,SC_Department c where 1=1
and a.SC_AddTime between #{time1,jdbcType=TIMESTAMP} and #{time2,jdbcType=TIMESTAMP}
and a.SC_DoorNO in (1,5,6,13,15,7,16)
and a.SC_DoorNO in (2,3,8,12,14,9)
and a.SC_DoorNO in (4,10,11)
and b.SC_DepartmentID=c.SC_DepartmentID and RIGHT('000'+convert(varchar(20),a.SC_CardGuidNO),10) = b.SC_MobileNO
and b.SC_WorkerNO = #{jobX,jdbcType=NVARCHAR}
and b.SC_Name = #{nameX,jdbcType=NVARCHAR}
and b.SC_DepartmentID = #{departmentx,jdbcType=INTEGER}
order by a.SC_CardGuidNO,a.SC_SerierNO
