mapper.select:
select g.*,f.SC_DeviceAreaName from (
select top 10 a.SC_SerierNO,a.SC_DoorNO,a.SC_CardGuidNO,a.SC_EventTypeID ,a.SC_InOutStatus,a.SC_AddTime ,b.SC_WorkerNO,b.SC_Name,b.SC_MobileNO,b.SC_IDTypeID,b.SC_DepartmentID ,c.SC_DepartmentName ,d.SC_CheckResultName ,e.SC_DeviceAreaID from SC_AccessRecord a ,SC_Employee b ,SC_Department c ,SC_ESDCheckResult d ,SC_Device e where a.SC_SerierNO > #{scSerierno,jdbcType=BIGINT} and b.SC_DepartmentID=c.SC_DepartmentID and b.SC_MobileNO like '%'+convert(varchar(20),a.SC_CardGuidNO) and a.SC_EventTypeID = d.SC_CheckResultID and a.SC_DoorNO=e.SC_SerierNO -- SC_SerierNO SC_DeviceSerierNO order by a.SC_SerierNO desc
) g,SC_DeviceArea f where g.SC_DeviceAreaID=f.SC_DeviceAreaID
